mapper.select:
select * from (
	select rownum rn, tmp.* from (
		select p.member_no,m.member_nick,p.report_no,p.photostory_comment_no no,p.c_report_reason reason 
		from comment_report p
		    inner join member m 
		    on m.member_no= p.member_no
	) tmp
) where rn between #{startRow} and #{endRow}
